  const imgSort = new Image(20, 20)
  const imgHide = new Image (20, 20)

  cell.append(imgSort)
  cell.append(imgHide)

  imgSort.src = sortByAlpha
  imgHide.src = visibilityOff

  imgSort.classList.add('imgSort')
